import React from "react";
import { useState } from "react";
import axios from "axios";

const GPTArea = () => {
  const [input, setInput] = useState("");
  const [response, setresponse] = useState("");
  const API_KEY = "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";

  const fetchData = async (input) => {
    const response = await axios.post(
      "https://api.openai.com/v1/completions",
      {
        prompt: `${input}`,
        model: "GPT-4",
        max_tokens: 50,
        n: 1,
        stop: ".",
      },
      {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${API_KEY}`,
        },
      }
    );

    return response.data.choices[0].text;
  };

  async function handleClick() {
    try {
      const response = await fetchData(input);
      setresponse(response);
    } catch (error) {
      console.error(error);
    }
  }

  return (
    <div className="container">
      <h2>Topline GPT</h2>
      <textarea
        value={input}
        onChange={(event) => setInput(event.target.value)}
        rows={5}
        placeholder="Type in some words and I'll finish the rest..."
      />
      <button className="button" onClick={handleClick}>
        Submit
      </button>
      {response && <p>Completed sentence: {response}</p>}
    </div>
  );
};

export default GPTArea;
